// Return url parameter/query string
function getParameterByName(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, "\\$&");
    var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
        results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, " "));
}

// live datetime
function liveDateTime (id) {
    var date = new Date;
    var year = date.getFullYear();
    var month = date.getMonth();
    var months = new Array('Jan', 'Feb', 'Mar', 'April', 'May', 'Jun', 'July', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec');
    var d = date.getDate();
    var day = date.getDay();
    var days = new Array('Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat');

    var hours = date.getHours();
    var minutes = date.getMinutes();
    var seconds =date.getSeconds();
    var dn = "PM";
    if (hours<12)
    dn = "AM";
    if (hours>12);
    hours = hours-12;
    if (hours==0)
    hours = 12;
    if (minutes <= 9)
    minutes="0"+minutes;
    if (seconds <= 9)
    seconds = "0"+seconds;
    var ctime = hours+":"+minutes+":"+seconds+" "+dn;
    document.getElementById(id).innerHTML = ''+days[day]+' '+d+' '+months[month]+' '+year+' '+ctime;
    setTimeout('liveDateTime("'+id+'");','1000');
    return true;
}

